// SPDX-License-Identifier: MIT

pragma solidity ^0.7.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";


// Disallow transfers of the token to or from blacklisted accounts.
abstract contract ERC20Whitelistable is ERC20, AccessControl, Ownable {
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant BURNER_ROLE = keccak256("BURNER_ROLE");

    constructor(address minter, address burner) {
        _setupRole(MINTER_ROLE, minter);
        _setupRole(BURNER_ROLE, burner);
    }

    
    event BlacklistUpdated(address indexed user, bool value);

    mapping(address => bool) public _blacklist;

    function blacklistUpdate(address user, bool value) public onlyOwner {
        _blacklist[user] = value;
        emit BlacklistUpdated(user, value);
    }

    modifier onlyWhitelisted(address account) {
        require(!_blacklist[account]);
        _;
    }

    modifier notWhitelisted(address account) {
        require(_blacklist[account]);
        _;
    }

    function transfer(address to, uint256 value)
        public
        virtual override(ERC20)
        onlyWhitelisted(msg.sender)
        onlyWhitelisted(to)
        returns (bool)
    {
        return super.transfer(to, value);
    }

    function transferFrom(address from, address to, uint256 value)
        public
        virtual override(ERC20)
        onlyWhitelisted(from)
        onlyWhitelisted(to)
        returns (bool)
    {
        return super.transferFrom(from, to, value);
    }

    function mint(address to, uint256 value) public  onlyWhitelisted(to) {
        require(hasRole(MINTER_ROLE, msg.sender), "Caller is not a minter");
        _mint(to, value);
    }

    function burn(address from, uint256 amount) public  onlyWhitelisted(from)  {
        require(hasRole(BURNER_ROLE, msg.sender), "Caller is not a burner");
        _burn(from, amount);
    }

    // Destroy the tokens held by a blacklisted account.
    function burnBlacklisted(address from, uint256 value)
        public
        notWhitelisted(from)
    {
        require(hasRole(BURNER_ROLE, msg.sender), "Caller is not a burner");
        _burn(from, value);
    }
}
